mapper.select:
select uid, score
from score
where subjectCode=#{subjectCode} and examName=#{examName}

	select m.grade, m.classnumber, m.studentnumber, u.uname
    from subject as s join member as m on s.subjectcode = m.subjectcode join user as u on m.uid=u.uid
    where s.subjectcode=#{subjectCode} and s.uid=#{uid};

select m.grade, m.classnumber, m.studentnumber, u.uname from subject as s inner join member as m on s.subjectcode=m.subjectcode inner join user as u on m.uid=u.uid where s.subjectcode=#{subjectCode} and s.uid=#{uid}
and m.grade like CONCAT('%', #{grade}, '%')
and m.classNumber like CONCAT('%', #{classNumber}, '%')
and m.studentNumber like CONCAT('%', #{studentNumber}, '%')
and u.uname like CONCAT('%', #{uname}, '%') ;

select subjectCode, examName, uid, score
from score
where subjectCode=#{subjectCode} and examName=#{examName} and uid=#{uid}
